mapper.select:
select id, title, description, start_time, end_time, status, full_score, sample_database_path from "assignment" where valid
and (status = 'public' or status = 'closed')
order by start_time desc offset #{offset} limit #{limit};

select count(*) from "assignment" where valid
and (status = 'public' or status = 'closed')
offset #{offset} limit #{limit};

select id, title, description, start_time, end_time, status, full_score, sample_database_path from "assignment" where valid
and (status = 'public' or status = 'closed')
and title like '%' || #{name} || '%' order by start_time desc offset #{param1.offset} limit #{param1.limit};

select count(*) from "assignment" where valid
and (status = 'public' or status = 'closed')
and title like '%' || #{name} || '%' offset #{param1.offset} limit #{param1.limit};

select id, title, description, start_time, end_time, status, full_score, sample_database_path from "assignment" where id = #{id}
and (status = 'public' or status = 'closed')
and valid;

mapper.update:
update "assignment"
set valid = false
where id = #{aid};

