mapper.select:
select id, problem_id, before_sql, after_sql, judge_script_id, answer, judge_database_id,dialect
from "judge_point"
where problem_id = #{id}
  and valid
order by id;

select id, problem_id, before_sql, after_sql, judge_script_id, answer, judge_database_id,dialect
from "judge_point"
where problem_id = #{id} and dialect= #{dialect}
  and valid
order by id;

mapper.update:
update judge_point
set valid = false
where id = #{id};
