mapper.select:
select id, assignment_id, title, description, full_score, space_limit, time_limit, number_submit, number_solve, index_in_assignment, solution from "problem" where valid
and (status = 'public' or status = 'closed')
order by id offset #{param1.offset} limit #{param1.limit};

select id, assignment_id, title, description, full_score, space_limit, time_limit, number_submit, number_solve, index_in_assignment, solution, type from "problem" where valid
and (status = 'public' or status = 'closed')
and id = #{id};

mapper.update:
update "problem"
set valid = false
where id = #{id};

update "problem"
set valid = true
where id = #{id};

update "problem"
set number_submit = #{numberSubmit},
    number_solve  = #{numberSolve}
where id = #{id};

update.if:
update "problem" set status = 'public' where valid and status = 'private'
    and assignment_id in
    (

        #{l}
    );

update "problem" set status = 'closed' where valid and status = 'public'
    and assignment_id in
    (

        #{l}
    );

mapper.select:
select id, assignment_id, title, description, full_score, space_limit, time_limit, number_submit, number_solve, index_in_assignment, solution from "problem" where valid and (status = 'public' or status = 'closed') and id in (select distinct problem_id from "problem_tag" where valid and tag_id in (
#{tag}
)) order by id offset #{param1.offset} limit #{param1.limit};

select count(*) over ()
