mapper.insert:
insert into "record" (code_id, user_id, problem_id, result, description, space, time, dialect, code_length)
VALUES (#{codeId}, #{userId}, #{problemId}, #{result}, #{description}, #{space}, #{time}, #{dialect},
        #{codeLength});

mapper.update:
update "record"
set valid = false
where id = #{id};

mapper.select:
select result as key, count(*) as count
from "record"
where valid
  and problem_id = #{id}
group by result;

select dialect as key, count(*) as count
from "record"
where valid
  and problem_id = #{id}
group by dialect;
