mapper.select:
select *
from "user"
where nickname = #{name}
   or username = #{name};

mapper.update:
update "user"
set nickname   = #{userForm.nickname},
    state_save = #{userForm.stateSave},
    lang       = #{userForm.lang}
where id = #{userId}

mapper.select:
select u.id, u.username, u.nickname, u.role, u.avatar
from "user" u
         join user_group ug on u.id = ug.user_id and u.valid and ug.valid
where ug.group_id = #{gid}
limit 50;

